function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }
	var acc_req = g_form.getValue('access_name');

    //Type appropriate comment here, and begin script below
    //ensure its not part of an order guide (not necessary but safety comes first so that if that bugs this doesnt break)
    if (g_form.getValue('order_guide') == 'false') {
		//first check if theirs only one value
		//ax
		if (acc_req.includes('f2d55e3f1b9f1050e7004229bc4bcbb7')) {

			top.window.onbeforeunload = null;
			top.window.location = "https://apigroupdev.service-now.com//sp?id=sc_cat_item&sys_id=4010858e1bbca410e7004229bc4bcb0a&view=sp";
