			top.window.location = "https://apigroupdev.service-now.com//sp?id=sc_cat_item&sys_id=094fc0b81bae38108c5143f3cc4bcb14&view=sp";
		}

		//webapps
		if (acc_req.includes('4a3bfcac1b679450e7004229bc4bcb42')) {


			//check company
			var u_company = g_form.getValue('user_company');
			var role_bool = false;
			//3d fire protection
			if(u_company == '7eb5dc9f1b021050f5a3635bbc4bcb56'){
				role_bool = true;
			}
			//api international llc
			else if(u_company == '810698531b421050f5a3635bbc4bcbf1'){
				role_bool = true;
			}
			//APi Systems Integrator
			else if(u_company == '16b59c9f1b021050f5a3635bbc4bcbb7'){
				role_bool = true;
			}
			//Arizona Verde Fire Protection, Inc.
			else if(u_company == '30c34bd1dbce34106be415ce3b96193b'){
				role_bool = true;
			}
			//Armor Fire Protection
			else if(u_company == '87b5dc9f1b021050f5a3635bbc4bcbec'){
				role_bool = true;	
			}
			//Branson Security and Fire
			else if(u_company == 'd0363d9f1b335c10e7004229bc4bcb23'){
				role_bool = true;
			}
			//Delta Fire Systems
			else if(u_company == 'dcb5d89f1b021050f5a3635bbc4bcb92'){
				role_bool = true;	
			}
			//Omlid & Swinney
			else if(u_company == 'd1d21dae1bda6490df31da49cc4bcb29'
				   || u_company == '26cd97211b683c10346ffd951a4bcb90'){
				role_bool = true;
			}
			//Premier Fire & Security, Inc.
			else if(u_company == '3df085aa1b9a3050346ffd951a4bcb94'){
				role_bool = true;	
			}	
			//Security Fire Protection Company Inc
			else if(u_company == '81b55c9f1b021050f5a3635bbc4bcb05'){
				role_bool = true;	
			}
